import "./ItemCount.css";
import { useState } from "react";
import { useCartContext } from "../Context/CartContext";

const ItemCount = ({ stock, item }) => {
  const { addProduct } = useCartContext();
  const [quantity, setQuantity] = useState(0);

  const increase = () => {
    if (stock > quantity) {
      setQuantity(quantity + 1);
    }
  };

  const decrease = () => {
    if (quantity > 0) {
      setQuantity(quantity - 1);
    }
  };

  const reset = () => {
    setQuantity(0);
  };

  const addToCart = () => {
    addProduct(item, quantity);
    reset();
  };

  return (
    <div>
      <div>
        <h2>{quantity}</h2>
        <button className="itemCountButton" onClick={decrease}>
          -
        </button>
        <button className="itemCountButton" onClick={increase}>
          +
        </button>
      </div>
      <div>
        <button className="itemCountButton" onClick={addToCart}>
          Agregar al carrito
        </button>
      </div>
    </div>
  );
};

export default ItemCount;
