import React from "react";
import { Link } from "react-router-dom";
import { useCartContext } from "../Context/CartContext";
import ItemCart from "../ItemCart/ItemCart";

const Cart = () => {
  const { cart, removeTCart, incrementCart, decrementCart } = useCartContext();

  const handleDelete = (id) => {
    removeTCart(id);
  };

  const onIncrementCart = (id) => {
    incrementCart(id);
  };
  const onDecrementCart = (id) => {
    decrementCart(id);
  };

  const calculateTotal = () => {
    // Usar reduce para sumar los precios de todos los productos en el carrito
    const total = cart.reduce((acc, item) => acc + item.price * item.qty, 0);
    return total.toFixed(0); // Ajustar a dos decimales si es necesario
  };
  return (
    <div>
      <div className="titleDetail">
        <h2>Detalle de compra</h2>
        <p>TOTAL:${calculateTotal()}</p>
        <Link to="/chekin">
          <button className="finishbuy">Finalizar compra</button>
        </Link>
      </div>
      <table className="cart-table">
        <thead>
          <tr>
            <th>#</th>
            <th>Producto</th>
            <th>precio</th>
            <th>cantidad</th>
            <th className="delete-column">Eliminar</th>
          </tr>
        </thead>
        <tbody>
          {cart.length > 0 ? (
            // Si el carrito tiene elementos, mapeamos sobre ellos
            cart.map((item) => (
              <ItemCart
                key={item.id}
                item={item}
                handleDelete={handleDelete}
                handleIncrement={onIncrementCart}
                handleDecrement={onDecrementCart}
              />
            ))
          ) : (
            <tr>
              <th colSpan="5" className="cartVacio">
                El carrito está vacío <br />{" "}
                <Link to="/" className="Link">
                  Hacer compras
                </Link>
              </th>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Cart;
