import React, { createContext, useContext, useState } from "react";

const CartContext = createContext();

export const useCartContext = () => {
  return useContext(CartContext);
};

const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);

  const addProduct = (product, quantity) => {
    // Lógica para agregar productos al carrito
    // Puedes modificar según tus necesidades y la estructura de tu producto
    setCart([...cart, { ...product, quantity }]);
  };

  const removeProduct = (productId) => {
    // Lógica para eliminar productos del carrito
    const updatedCart = cart.filter((item) => item.id !== productId);
    setCart(updatedCart);
  };

  const clearCart = () => {
    // Lógica para vaciar el carrito
    setCart([]);
  };

  const getTotalItems = () => {
    // Lógica para obtener la cantidad total de elementos en el carrito
    return cart.reduce((total, item) => total + item.quantity, 0);
  };

  const getTotalPrice = () => {
    // Lógica para calcular el precio total del carrito
    return cart.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  return (
    <CartContext.Provider
      value={{
        cart,
        addProduct,
        removeProduct,
        clearCart,
        getTotalItems,
        getTotalPrice,
      }}
    >
      {children}
    </CartContext.Provider>
  );
};

export default CartProvider;
