import React from "react";
import { Link } from "react-router-dom";
import { useState } from "react";
import { useCartContext } from "../Context/CartContext";
import {
  getFirestore,
  collection,
  addDoc,
  updateDoc,
  doc,
  getDoc,
} from "firebase/firestore";
import "./Checkout.css";

export const Checkout = () => {
  const [name, setName] = useState("");
  const [surname, setSurname] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [email, setEmail] = useState("");
  const [confirmationEmail, setConfirmationEmail] = useState("");
  const [error, setError] = useState("");
  const [idOrder, setIdOrder] = useState("");

  const { cart, removeProduct, totalPrice } = useCartContext();

  const formManager = (event) => {
    event.preventDefault();

    if (!name || !surname || !phoneNumber || !email || !confirmationEmail) {
      setError("Por favor completa todos los campos");
      return;
    }

    if (email !== confirmationEmail) {
      setError("Los campos de email no coinciden");
      return;
    }
    const total = totalPrice();
    const orden = {
      items: cart.map((producto) => ({
        id: producto.id,
        name: producto.title,
        cantidad: producto.quantity,
      })),
      total: total,
      fecha: new Date(),
      name,
      surname,
      phoneNumber,
      email,
    };

    Promise.all(
      orden.items.map(async (productoOrden) => {
        const db = getFirestore();
        const productoRef = doc(db, "products", productoOrden.id);

        const productoDoc = await getDoc(productoRef);
        const stockActual = productoDoc.data().stock;

        await updateDoc(productoRef, {
          stock: stockActual - productoOrden.cantidad,
        });
      })
    )
      .then(() => {
        const db = getFirestore();
        addDoc(collection(db, "orders"), orden)
          .then((docRef) => {
            setOrdenId(docRef.id);
            removeProduct();
          })
          .catch((error) => {
            console.log("Error en creacon de orden", error);
            setError("Error en orden");
          });
      })
      .catch((error) => {
        console.log("No se puede actualizar el stock", error);
        setError("No se actualizo el stock");
      });

    setname("");
    setsurname("");
    setphoneNumber("");
    setEmail("");
    setconfirmationEmail("");
  };

  return (
    <>
      <h2 className="info">
        Rellena el formulario y nos contactaremos para enviar sus productos
      </h2>

      <form onSubmit={manejadorFormulario}>
        {cart.map((producto) => (
          <div className="item-check" key={producto.id}>
            <p>
              {" "}
              {producto.name} {producto.cantidad}
            </p>
            <p> {producto.precio} </p>
          </div>
        ))}

        <div className="form-group">
          <label className="lab-check">name</label>
          <input
            className="input-check"
            type="text"
            value={name}
            onChange={(e) => setname(e.target.value)}
          />
        </div>

        <div className="form-group">
          <label className="lab-check">surname</label>
          <input
            className="input-check"
            type="text"
            value={surname}
            onChange={(e) => setsurname(e.target.value)}
          />
        </div>

        <div className="form-group">
          <label className="lab-check">phoneNumber</label>
          <input
            className="input-check"
            type="number"
            value={phoneNumber}
            onChange={(e) => setphoneNumber(e.target.value)}
          />
        </div>

        <div className="form-group">
          <label className="lab-check">Email</label>
          <input
            className="input-check"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>

        <div className="form-group">
          <label className="lab-check">Email Confirmacion</label>
          <input
            className="input-check"
            type="email"
            value={confirmationEmail}
            onChange={(e) => setconfirmationEmail(e.target.value)}
          />
        </div>

        {error && <p className="error-campos">{error}</p>}

        {ordenId && (
          <p className="orden">
            Â¡Gracias por tu compra! <br /> Este es tu numero de orden: <br />{" "}
            {ordenId}{" "}
          </p>
        )}

        <div className="checking">
          <button className="check-bt" type="submit">
            Finalizar Compra
          </button>
        </div>
        <br />
        <div>
          <Link to="/">
            <button className="back-to-home">Volver al Home</button>
          </Link>
        </div>
      </form>
    </>
  );
};

export default Checkout;
