const products = [
  {
    id: "1",
    name: "iPhone 12",
    price: 1000,
    category: "celular",
    img: "https://hiperlibertad.vtexassets.com/arquivos/ids/205166/CELULAR-XIAOMI-REDMI-NOTE-11-BLUE-128-GB-1-37050.jpg?v=638088969847970000",
    stock: 25,
    description: "Descripción de Iphone 12",
  },
  {
    id: "2",
    name: "iPhone 11",
    price: 800,
    category: "celular",
    img: "https://hiperlibertad.vtexassets.com/arquivos/ids/205166/CELULAR-XIAOMI-REDMI-NOTE-11-BLUE-128-GB-1-37050.jpg?v=638088969847970000",
    stock: 20,
    description: "Descripción de Iphone 12",
  },
  {
    id: "3",
    name: "Tablet",
    price: 1100,
    category: "tablet",
    img: "https://hiperlibertad.vtexassets.com/arquivos/ids/205166/CELULAR-XIAOMI-REDMI-NOTE-11-BLUE-128-GB-1-37050.jpg?v=638088969847970000",
    stock: 25,
    description: "Descripción de Iphone 12",
  },
];

export const getProducts = () => {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve(products);
    }, 500);
  });
};

export const getProductById = (productId) => {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve(products.find((prod) => prod.id === productId));
    }, 500);
  });
};

export const getProductsByCategory = (productCategory) => {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve(products.find((prod) => prod.category === productCategory));
    }, 500);
  });
};
